import React, { useState } from 'react';
import { usePagesContext } from '../hooks/usePagesContext'
import TagInputForm from './TagInputForm';

const CreatePageForm = () => {
    const { dispatch } = usePagesContext()
    const [title, setTitle] = useState('')
    const [tags, setTags] = useState([])
    const [error, setError] = useState(null)

    const handleSubmit = async (e) => {
        console.log('Submit requested') // Debugging log
        e.preventDefault()

        const page = { title, tags }

        try {
            const response = await fetch('/api/pages', {
                method: 'POST',
                body: JSON.stringify(page),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const json = await response.json()

            if (!response.ok) {
                console.log('Found error in submitting form')
                setError(json.error)
            } 
            if (response.ok) {
                setError(null)
                // Reset form
                setTitle('')
                setTags([])

                console.log('Added page')
                dispatch({ type: 'CREATE_PAGE', payload: json })
            }
        } catch (err) {
            console.error('Submission failed', err)
            setError('Something went wrong!')
        }
    }

    const preventEnterSubmit = (e) => {
        if (e.key === 'Enter') e.preventDefault()
    }

    const handleKeyDown = (e) => {
        console.log('Trying to add tag, keydown:', e.key)
        if (e.key !== 'Enter') return
        const value = e.target.value
        if (!value.trim()) return
        setTags([...tags, value])
        e.target.value = ''
    }

    const handleDelete = (index) => {
        console.log('Trying to delete tag')
        setTags(tags.filter((el, i) => i !== index))
    }

    return (
        <form className="inputs" onKeyDown={preventEnterSubmit} onSubmit={handleSubmit}>
            <input 
                type="text"
                onChange={(e) => setTitle(e.target.value)}
                value={title}
                placeholder="Page Title"
            />
            {/* Was originally TagInputForm, manually created to clear tags on submission */}

            <div className='tags-container'>
                {tags.map((tag, index) => (
                    <div className="tag-item" key={index}>
                        <span className="text">{tag}</span>
                        <span onClick={() => handleDelete(index)} className="close">&times;</span>
                    </div>
                ))}
                <input className='tags-input' onKeyDown={handleKeyDown} placeholder={'Type something'}></input>
            </div>

            <button type="submit">Add Page</button> {/* Button for form submission */}
            {error && <div className="error">{error}</div>}
        </form>
    )
}

export default CreatePageForm;
