import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PageContent.css';

// Main space for where content/notes are written/stored for each page
// Customizable with text editing, commands, formatting, and elements

const PageContent = () => {
  const { id } = useParams();
  const [content, setContent] = useState('');
  const [input, setInput] = useState('');

  // Fetch content from the server on mount
  useEffect(() => {
    const fetchContent = async () => {
      try {
        const response = await fetch(`/pages/${id}`);
        const data = await response.json();
        if (response.ok) {
          setContent(data.content);
          document.getElementById('page-content').innerHTML = data.content;
        } else {
          console.error('Failed to fetch page content:', data);
        }
      } catch (err) {
        console.error('Error:', err);
      }
    };

    fetchContent();
  }, [id]);

  // useEffect that runs whenever the 'input' changes
  useEffect(() => {
    const checkForCommands = (value) => {
      const regex = /\/(\w+)/g;
      let match;
      while ((match = regex.exec(value)) !== null) {
        handleCommand(match[1]);
      }
    };

    checkForCommands(input);
  }, [input]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
  };

  const handleCommand = (command) => {
    switch (command) {
      case "bold":
        console.log("Making text bold...");
        // Add your bold text logic here
        break;
      // Add more commands as needed
      default:
        console.log(`Command ${command} doesn't exist/isn't finished`);
    }
  };

  return (
    <textarea
      name="page-content"
      className="page-content-container"
      onChange={handleInputChange}
      value={input}
    />
  );
};

export default PageContent;
