import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PageContent.css';
import CommandHandler from './CommandHandler';
import { PagesContext } from './PathToYourContextFile'; // Update this path to your actual context file

const PageContent = () => {
  const { id } = useParams();
  const { pages, dispatch } = useContext(PagesContext);
  const [input, setInput] = useState('');

  // Fetch content from the context on mount
  useEffect(() => {
    const currentPage = pages.find(page => page._id === id);
    if (currentPage) {
      setInput(currentPage.content || '');
    }
  }, [id, pages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  // Save content to the context and backend when it changes
  useEffect(() => {
    if (input) {
      const currentPage = pages.find(page => page._id === id);
      if (currentPage) {
        dispatch({
          type: 'UPDATE_PAGE',
          payload: { ...currentPage, content: input }
        });

        const updatePageContent = async () => {
          try {
            await fetch(`/pages/${id}`, {
              method: 'PUT',
              headers: {
                'Content-Type': 'application/json'
              },
              body: JSON.stringify({ content: input })
            });
          } catch (err) {
            console.error('Failed to update page content:', err);
          }
        };

        updatePageContent();
      }
    }
  }, [input, id, pages, dispatch]);

  return (
    <>
      <textarea
        name="page-content"
        className="page-content-container"
        onChange={handleInputChange}
        value={input}
        placeholder="Type a message or command"
      />
      <CommandHandler input={input} setInput={setInput} />
    </>
  );
};

export default PageContent;
