import { useState } from 'react';
import '../styles/Dropdown.css';

const Dropdown = (isOpen) => {

    const handleOpen = () => {
        isOpen = !isOpen;
    }

    return (
        <div className="dropdown-container">
            <div className={`arrow ${isOpen ? 'open' : ''}`} onClick={handleOpen}/>
        </div>
    )
}

export default Dropdown;
