import React, { useEffect, useState } from "react";

const CommandHandler = ({ input, setInput, editor }) => {
    const [isBold, setIsBold] = useState(false)


    useEffect(() => {
        const checkForCommands = (value) => {
            const regex = /\/(\w+)/g;
            let match;

            while ((match = regex.exec(value)) !== null) {
                handleCommand(match[1], match.index);
            }
        };

        const clear = ( command, index ) => {
            const before = input.slice(0, index);
            const after = input.slice(index + command.length + 1);
            setInput(`${before}${after}`);
        }

        const replace = ( command, index, insert ) => {
            const before = input.slice(0, index);
            const after = input.slice(index + command.length + 1);
            setInput(`${before}${insert}${after}`);
        }

        const handleCommand = (command, index) => {
            switch (command) {
                case "d20":
                    console.log(
                        "Rolled a d20: ",
                        Math.floor(Math.random() * 20) + 1
                    );
                    let insert = String(Math.floor(Math.random() * 20) + 1);
                    replace(command, index, insert);
                    break;
                // Add more commands as needed
                case "bold":
                    console.log('triggering bold');
                    editor.format('bold', true);
                    clear(command, index);
                    break;
                default:
                    console.log(
                        `Command ${command} doesn't exist/isn't finished`
                    );
            }
        };
        checkForCommands(input);
    }, [input]);

    // Runs through body of text and converts all instances of text in between ** ** to bold characters
    const toggleBold = () => {
        // get the selection window
        const selection = window.getSelection();
        if (selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            const text = range.toString();

            if (text) {
                const beg = text.slice(0, range.startOffset(0));
                const end = text.slice(range.endOffset);
                const newInput = `${beg}**${text}**${end}`;

                setInput(newInput);
                range.deleteContents();
                range.insertNode(document.createTextNode(`**${text}**`));
                selection.removeAllRanges();
            } else {
                // If no selection is made
                const cursorPos = window.anchorOffset();
                const beg = input.slice(0, cursorPos);
                const end = input.slice(cursorPos);
                setIsBold(!isBold);
                const newInput = isBold ? `${beg}**` : input
                setInput(newInput);
            }
        }
    }

    // Handles commands such as Ctrl+b and externally inputted commands
    const handleKeyDown = (e) => {
        if (e.ctrlKey && e.key === 'b') {
            e.preventDefault();
            console.log("Bold triggered");
            toggleBold();
        }
    }

    useEffect(() => {
        console.log("Noticed a keydown events")
        document.addEventListener('keydown', handleKeyDown);
        return () => {
            document.removeEventListener('keydown', handleKeyDown);
        }
    }, [input, isBold])

    return null;
};

export default CommandHandler;
