import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PageContent.css';
import CommandHandler from '../handlers/CommandHandler';
import { PagesContext } from '../contexts/PagesContext'; // Update this path to your actual context file
import * as api from '../api'; // Import the function
import { ReactQuill, Quill, customBoldHandler } from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import '../styles/QuillEditor.css';
import CustomToolbarModule from '../modules/CustomToolbarModule';

Quill.register('modules/customToolbar', CustomToolbarModule);

const PageContent = () => {
  const { id } = useParams();
  const { pages, dispatch } = useContext(PagesContext);
  const [input, setInput] = useState('');
  const [error, setError] = useState(null);

  const handleInputChange = async (e) => {
    const newContent = e.target.value;
    setInput(newContent);
  
    try {
      await api.updatePageContent(id, newContent, dispatch);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    console.log("Loading page")
    const fetchContent = async () => {
      try {
        setInput(await api.fetchPageContent(id));
      } catch (err) {
        console.log(err);
      }
    }
    
    fetchContent();
  }, [id]);

  const customToolbar = [
    [{ 'header': '1' }, { 'header': '2' }, { 'font': [] }],
    [{ size: [] }],
    ['bold', 'italic', 'underline', 'strike', 'blockquote'],
    [{ 'list': 'ordered' }, { 'list': 'bullet' },
      { 'indent': '-1' }, { 'indent': '+1' }],
    ['link', 'image', 'video'],
    ['clean'],
  ];
  
  const modules = { 
    customToolbar: true, // Enable the custom toolbar module 
    toolbar: customToolbar, // Set the toolbar configuration
  };

  Quill.register('modules/QuillCustomModule', modules);

  return (
    <>
      {/* <textarea
        name="page-content"
        className="page-content-container"
        onChange={handleInputChange}
        value={input}
        placeholder="Type a message or command"
      /> */}
      <ReactQuill
        theme="snow"
        className="temp"
        value={input}
        onChange={setInput}
        modules={modules}
        placeholder="Type a message or roll a dice"
      >

      </ReactQuill>
      <CommandHandler input={input} setInput={setInput} />
      {error && <p className="error-message">{error}</p>} {/* Display error message */}
    </>
  );
};

export default PageContent;
