import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PageContent.css';
import CommandHandler from '../handlers/CommandHandler';

// Main space for where content/notes are written/stored for each page
// Customizable with text editing, commands, formatting, and elements

const PageContent = () => {
  const { id } = useParams();
  const { pages, dispatch } = useContext(PagesContext);
  const [content, setContent] = useState('');
  const [input, setInput] = useState('');

  // Fetch content from the server on mount
  useEffect(() => {
    const fetchContent = async () => {
      try {
        const response = await fetch(`/pages/${id}`);
        const data = await response.json();
        if (response.ok) {
          setInput(data.content);
          document.getElementById('page-content').innerHTML = data.content;
        } else {
          console.error('Failed to fetch page content:', data);
        }
      } catch (err) {
        console.error('Error:', err);
      }
    };

    fetchContent();
  }, [id]);

  // Save content to the server when it changes
  // Save content to the context and backend when it changes
  useEffect(() => {
    if (input) {
      dispatch({ type: 'UPDATE_PAGE', payload: { _id: id, content: input } });

      const updatePageContent = async () => {
        try {
          await fetch(`/pages/${id}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ content: input })
          });
        } catch (err) {
          console.error('Failed to update page content:', err);
        }
      };

      updatePageContent();
    }
  }, [input, id, dispatch]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  return (
    <>
      <textarea
        name="page-content"
        className="page-content-container"
        onChange={handleInputChange}
        value={input}
        placeholder="Type a message or command"
      />
      <CommandHandler input={input} setInput={setInput} />
    </>
  );
};

export default PageContent;
