import React, { useState } from 'react';
import { usePagesContext } from '../hooks/usePagesContext'

const DeletePageButton = ({ page }) => {
    const [error, setError] = useState(null);
    const { dispatch } = usePagesContext()

    const handleSubmit = async () => {
        try {
            const response = await fetch('/api/pages', {
                method: 'POST',
                body: JSON.stringify(page),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const json = await response.json()

            if (!response.ok) {
                console.log('Found error in deleting page')
                setError(json.error)
            } 
            if (response.ok) {
                setError(null)

                console.log('Deleted page')
                dispatch({ type: 'DELETE_PAGE', payload: json })
            }
        } catch (err) {
            console.error('Submission failed', err)
            setError('Something went wrong!')
        }
    }

    return (
        <div className="deleteButton">
            <button onClick={handleSubmit}>Delete Page</button>
            {error && <div className="error">{error}</div>}
        </div>
    )
}

export default DeletePageButton;
