import React, { useState } from 'react';
import { usePagesContext } from '../hooks/usePagesContext'
import TagInputForm from './TagInputForm';

const CreatePageForm = () => {
    const { dispatch } = usePagesContext()
    const [title, setTitle] = useState('')
    const [tags, setTags] = useState([])
    const [error, setError] = useState(null)

    const handleSubmit = async (e) => {
        console.log('Submit requested') // Debugging log
        e.preventDefault()

        const page = { title, tags }

        try {
            const response = await fetch('/api/pages', {
                method: 'POST',
                body: JSON.stringify(page),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const json = await response.json()

            if (!response.ok) {
                console.log('Found error in submitting form')
                setError(json.error)
            } 
            if (response.ok) {
                setError(null)
                // Reset form
                setTitle('')
                setTags([])

                console.log('Added page')
                dispatch({ type: 'CREATE_PAGE', payload: json })
            }
        } catch (err) {
            console.error('Submission failed', err)
            setError('Something went wrong!')
        }
    }

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') return
    }

    return (
        <div className="inputs" >
            <input 
                type="text"
                onChange={(e) => setTitle(e.target.value)}
                value={title}
                placeholder="Page Title"
            />
            <TagInputForm />
            <button onSubmit={handleSubmit} type="submit">Add Page</button> {/* Button for form submission */}
            {error && <div className="error">{error}</div>}
        </div>
    )
}

export default CreatePageForm;
