import React from 'react';
import { NavLink } from 'react-router-dom';
import Dropdown from './Dropdown';

const PageListElem = ({ page, level, toggleExpand, expandedPages }) => {
  const hasChildren = Array.isArray(page.children) && page.children.length > 0;
  const isExpanded = expandedPages.includes(page._id);

  return (
    <div>
      <div
        style={{ paddingLeft: `${level * 20}px`, cursor: hasChildren ? 'pointer' : 'default' }}
        onClick={() => hasChildren && toggleExpand(page._id)}
      >
        {hasChildren && <Dropdown isOpen={isExpanded} />}
        <NavLink className="page-list-elem-link" to={`/page/${page._id}`}>
          {page.title}
        </NavLink>
      </div>
      {isExpanded && hasChildren && (
        <div>
          {page.children.map(child => (
            <PageListElem
              key={child._id}
              page={child}
              level={level + 1}
              toggleExpand={toggleExpand}
              expandedPages={expandedPages}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default PageListElem;
