import React, { useState, useEffect, useRef } from 'react';
import '../styles/LeftSidebar.css';
import { usePagesContext } from '../hooks/usePagesContext';
import { NavLink, Outlet, useParams } from 'react-router-dom';

// components
import ContextMenu from './ContextMenu';
import DeletePageButton from './DeletePageButton';
import RenamePageButton from './RenamePageButton';

/* Contains the list of pages available on the left side, from their highest level
    and sortable. To be created:

    Framework
    Create section/folder (not a page, just a sortation tool)
        Creating a section will create a button with a label that expands all pages
        stored in said section
        Section Page (as mentioned below) will allow for a folder as well a front page for
        the pages in the section, such as a country and then all of its city pages
        listed below
    Create section page (potentially add "section: boolean" to Page.js)
    Dropdown for each section
*/

const LeftSidebar = () => {
  const { pages } = usePagesContext();
  const [contextMenu, setContextMenu] = useState({
    position: {
        x: 0,
        y: 0,
    },
    toggled: false,
    page: null
  })
  const [renameId, setRenameId] = useState('');
  const [editTitle, setEditTitle] = useState('');

  useEffect(() => {
    const handleClick = () => setContextMenu({
        position: {
            x: contextMenu.position.x,
            y: contextMenu.position.y,
        },
        toggled: false,
        page: null
    })
    window.addEventListener('click', handleClick)
    return () => window.removeEventListener('click', handleClick)
  }, [])

  const handleRightClick = (e, page) => {
    console.log('right clicked')
    e.preventDefault()

    const x = e.clientX
    const y = e.clientY

    setContextMenu({
        position: {
            x,
            y,
        },
        toggled: true,
        page: page
    })
  }

  const handleRenameSubmit = async (e) => {
    if (e.key === 'Enter') {
        // Submit the title, send PATCH request, etc - initially copied from RenamePageButton
        try {
            console.log('attempting to rename page:', renameId);
            const response = await fetch('/pages/' + renameId, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ title: editTitle })
            });

            if (!response.ok) {
                const errorJson = await response.json();
                console.log('Found error in updating page:', errorJson);
                setError(errorJson.error || 'Failed to update page');
                return;
            } 

            const updatedPage = await response.json();
            setError(null);
            console.log('Updated page');
            dispatch({ type: 'UPDATE_PAGE', payload: updatedPage });
        } catch (err) {
            console.error('Submission failed', err);
            setError('Something went wrong!');
        }
    }
  }

  return (
    <div className="sidebar-container">
        <div className="logo">
            <NavLink to={'/page/home'} className="text">LMNT</NavLink>
        </div>
        <NavLink className="create-page-button" to={'/page/new'}>Create Page</NavLink>

        {/* Loop through the pages, and if the current pageid matches the id of the rename-page, then instead 
            of a NavLink, put an input button. On submit, delete the rename-page id and the map will display
            as a NavLink once again
        */}

        {pages && pages.map((page) => (
            <div 
                onContextMenu={(e) => {handleRightClick(e, page)}}
                key={page._id} 
            >
                {page._id === renameId ? (
                    <input
                        type="text"
                        onKeyDown={handleRenameSubmit}
                        autoFocus
                        onBlur={() => setRenameId('')}
                        onChange={(e) => setEditTitle(e.target.value)}
                    >
                        
                    </input>
                ) : (
                    <NavLink 
                    className="page-link" 
                    to={'/page/' + page._id}
                >
                    {page.title}
                </NavLink>
                )}
            </div>
        ))}
        {contextMenu.toggled && <ContextMenu 
            isVisible={contextMenu.toggled}
            position={contextMenu.position}
            page={contextMenu.page}
        >
            <RenamePageButton page={contextMenu.page} title={"Updated Page"} className="context-menu-button">New Page</RenamePageButton>
            <DeletePageButton page={contextMenu.page} className="context-menu-button">Delete Page</DeletePageButton>
            <button className="context-menu-button">Rename Page</button>

        </ContextMenu>
        }
    </div>    
  );
};

export default LeftSidebar;
