import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/PageContent.css';
import CommandHandler from '../handlers/CommandHandler';
import { PagesContext } from '../contexts/PagesContext'; // Update this path to your actual context file

const PageContent = () => {
  const { id } = useParams();
  const { pages, dispatch } = useContext(PagesContext);
  const [input, setInput] = useState('');
  const [error, setError] = useState(null);

  const handleInputChange = async (e) => {
    const newValue = e.target.value;
    setInput(newValue);
  
    try {
      const response = await fetch('/pages/{id}', {
        method: "POST",
        body: JSON.stringify(input),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      const json = await response.json();

      if (response.ok) {
        console.log("Updating page");
        dispatch({ type: 'UPDATE_PAGE', payload: json });
      } else {
        console.log('Found error in submitting form', error);
        setError(json.error);
      }
    } catch (err) {
      console.log("Couldn't save, got this error: ", err);
    }
  };

  useEffect(() => {
    console.log("Loading page")
    const currentPage = pages.find(page => page._id === id);
    if (currentPage) {
      setInput(currentPage.content || '');
    }
  }, [id, pages]);

  return (
    <>
      <textarea
        name="page-content"
        className="page-content-container"
        onChange={handleInputChange}
        value={input}
        placeholder="Type a message or command"
      />
      <CommandHandler input={input} setInput={setInput} />
    </>
  );
};

export default PageContent;
