import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { usePagesContext } from '../hooks/usePagesContext';
import '../styles/Page.css';

// components

const Page = () => {
  const { tags, id } = useParams(); // Get the unique identifier from the URL
  const [pageData, setPageData] = useState(null);
  const { pages, dispatch } = usePagesContext();
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [content, setContent] = useState('');

  useEffect(() => {
    // Fetch page data from the server or MongoDB using the id
    fetch('/pages/' + id)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched page data:', data); // Debugging log
        setPageData(data);
        setContent(data.content);
      })
      .catch(error => {
        console.error('Error fetching page data:', error);
        setError('Error fetching page data');
      });
  }, [id]);

  if (!pageData) return <div>Loading...</div>;

  const handleDeletePage = async () => {    
    try {
      const response = await fetch('/pages/' + id, {
        method: 'DELETE',
      });
      const json = await response.json();

      if (!response.ok) {
        console.log('Found error in deleting page');
        setError(json.error);
      }

      dispatch({ type: 'DELETE_PAGE', payload: id });
      navigate('/'); // Redirect to homepage or another route after deletion

    } catch (err) {
      console.error('Submission failed', err);
      setError('Something went wrong!');
    }
  };

  const handleContentChange = async (e) => {
    const newContent = e.target.value;
    setContent(newContent);

    try {
      const response = await fetch('/pages/' + id, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ content: newContent })
      });

      if (!response.ok) {
        const json = await response.json();
        console.log('Found error in updating page content');
        setError(json.error);
      } else {
        const updatedPage = await response.json();
        dispatch({ type: 'UPDATE_PAGE', payload: updatedPage })
      }
    } catch (err) {
      console.log(err)
    }
  }

  // Deletes target tag
  const handleTagDelete = (index) => {
    console.log('Trying to delete tag');
    setTags(tags.filter((el, i) => i !== index));
};

  return (
    <div className="current-page-container">
      {/* Page Info */}
      <h1>{pageData.title}</h1>
      <div className='tags-container'>
                {tags.map((tag, index) => (
                    <div className="tag-item" key={index}>
                        <span className="text">{tag}</span>
                        <span onClick={() => handleTagDelete(index)} className="close">&times;</span>
                    </div>
                ))}
                <input className='tags-input' onKeyDown={handleKeyDown} placeholder={'Tag name'}></input>
            </div>
      <p>ID: {id}</p>
      {pageData.parent ? <p>Parent ID: {pageData.parent.title}</p> : <p>No parent</p>}
      <p>Children:</p>
      {pageData.children && pageData.children.length > 0 ? (
        pageData.children.map((child) => (
          <p key={child._id}>Child: {child._id}</p>
        ))
      ) : (
        ''
      )}

      {/* Page layout and content area */}
      <textarea 
        name="main-content" 
        id="main-content" 
        className="page-content-container" 
        value={content}
        onChange={handleContentChange}
      />

      {/* Render other page details */}
      <button className="delete-page-button" onClick={handleDeletePage}>Delete Page</button>
    </div>
  );
};

export default Page;
