import React, { useState, useEffect, useRef } from 'react';
import '../styles/LeftSidebar.css';
import { usePagesContext } from '../hooks/usePagesContext';
import { NavLink, Outlet } from 'react-router-dom';

// components
import Menu from '../components/Menu';

const LeftSidebar = () => {
  const { pages } = usePagesContext();
  const contextMenuRef = useRef(null);
  const [contextMenu, setContextMenu] = useState({
    position: {
        x: 0,
        y: 0,
    },
    toggled: false,
    page: null
  });

  useEffect(() => {
    const handleClick = () => setContextMenu({
      position: {
          x: contextMenu.position.x,
          y: contextMenu.position.y,
      },
      toggled: false,
      page: null
    });
    window.addEventListener('click', handleClick);
    return () => window.removeEventListener('click', handleClick);
  }, [contextMenu.position.x, contextMenu.position.y]);

  const handleRightClick = (e, page) => {
    e.preventDefault();

    const x = e.clientX;
    const y = e.clientY;

    setContextMenu({
        position: {
            x,
            y,
        },
        toggled: true,
        page: page
    });
  };

  return (
    <div className="sidebar-container">
      <div className="logo">
        <NavLink to={'/page/home'} className="text">LMNT</NavLink>
      </div>
      <NavLink className="create-page-button" to={'/page/new'}>Create Page</NavLink>
      {pages && pages.map((page) => (
        <div 
          onContextMenu={(e) => handleRightClick(e, page)}
          key={page._id}
          className="page-link"
        >
          {page.title}
        </div>
      ))}
      {contextMenu.toggled && (
        <Menu style={{ top: `${contextMenu.position.y}px`, left: `${contextMenu.position.x}px` }}>
          <div>
            <p>Page Title: {contextMenu.page.title}</p>
            <button onClick={() => console.log(`Editing ${contextMenu.page.title}`)}>Edit Page</button>
            <button onClick={() => console.log(`Deleting ${contextMenu.page.title}`)}>Delete Page</button>
          </div>
        </Menu>
      )}
    </div>
  );
};

export default LeftSidebar;
