import '../styles/Menu.css';

const Menu = ({ x, y, showMenu }) => {

    const style = () => {
        return { 
            height: 200, 
            width: 150,
            borderRadius: 10,
            backgroundColor: `#FF5C58`,
            color: "#FCD2D1",
            flexDirection: 'column',
            padding: 10,
            top: y,
            left: x,
            position: 'absolute',
            display: showMenu ? 'flex' : 'none',
        }
    }
    
    return (
        <div style={style()}>
            <div>Button 1</div>
            <div>Button 2</div>
            <div>Button 3</div>
        </div>
    )
}

export default Menu;
