import React, { useState, useEffect } from 'react';
import '../styles/Menu.css';

const Menu = ({ 
    contextMenuRef,
    rightClickItem,
    positionX,
    positionY,
    isToggled, 
}) => {
    return (
        <menu style={{
            top: positionY + 2 +'px',
            left: positionX + 2 +'px',
        }}
        className={`context-menu ${isToggled ? 'active' : ''}`}
        ref={contextMenuRef}>
            <text>hello world</text>
            <text>poop</text>
        </menu>

    );
};

export default Menu;
